import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, TextField, Button, Grid, Paper } from '@mui/material';

const templatesData = {
  1: { name: 'Classic Resume', fields: { name: '', email: '', phone: '', skills: '' } },
  2: { name: 'Modern Resume', fields: { name: '', summary: '', experience: '' } },
  3: { name: 'Creative Resume', fields: { name: '', portfolio: '', hobbies: '' } },
};

const EditTemplate = () => {
  const { id } = useParams();
  const [formData, setFormData] = useState(templatesData[id]?.fields || {});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Template saved successfully!');
  };

  return (
    <Box sx={{ padding: 4 }}>
      <Typography variant="h3" align="center" gutterBottom>
        Edit {templatesData[id]?.name || 'Template'}
      </Typography>
      <Paper elevation={3} sx={{ padding: 4, maxWidth: 600, margin: '0 auto' }}>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {Object.keys(formData).map((field) => (
              <Grid item xs={12} key={field}>
                <TextField
                  label={field.charAt(0).toUpperCase() + field.slice(1)}
                  name={field}
                  value={formData[field]}
                  onChange={handleChange}
                  fullWidth
                />
              </Grid>
            ))}
          </Grid>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ marginTop: 2 }}
          >
            Save Template
          </Button>
        </form>
      </Paper>
    </Box>
  );
};

export default EditTemplate;
